/**
 * detects if the given key have a circular dependency within.
 * @param o object
 * @param key key
 * @param visited additional objects to test against
 */
export declare const keyHasCircularDependency: <T extends object>(o: T, key: keyof T, visited?: Set<unknown>) => boolean;
/**
 * detects if an object/array have a circular dependency within its structure.
 * @param o object
 */
export declare const hasCircularDependency: (o: unknown) => boolean;
