/**
 * creates a new object with only the given keys.
 * @param object source.
 * @param keys keys to preserve
 */
export default function pick(object, ...keys) {
    const out = {};
    Object.keys(object).forEach((key) => {
        if (keys.includes(key)) {
            out[key] = object[key];
        }
    });
    return out;
}
